SELECT * FROM t_banner_info t
AND t.pk_id = #{bannerInfoId, jdbcType=INTEGER}
LIMIT 1

mapper.update:
UPDATE t_banner_info t
t.update_user = #{bannerInfo.updateUser}, t.update_time = #{bannerInfo.updateTime}, t.data_version = #{bannerInfo.dataVersion}, t.banner_name = #{bannerInfo.bannerName}, t.banner_src = #{bannerInfo.bannerSrc}, t.detail = #{bannerInfo.detail}, t.state = #{bannerInfo.state}, t.banner_location = #{bannerInfo.bannerLocation},
